CREATE TABLE `article_article` (`article_id` varchar(100) NOT NULL PRIMARY KEY, `article_url` varchar(200) NOT NULL, `article_user` varchar(100) NOT NULL, `article_user_url` varchar(200) NOT NULL, `article_image` varchar(200) NOT NULL, `article_title` varchar(100) NOT NULL, `article_body` longtext NULL, `article_time` varchar(100) NULL, `article_views` varchar(100) NOT NULL, `article_comments` varchar(100) NOT NULL, `article_likes` varchar(100) NOT NULL, `article_tip` varchar(100) NOT NULL, `created` datetime(6) NOT NULL);
